INSERT INTO content (id, title, introduction, path, author_id, publish_date, dtype) VALUES (nextval('content_sequence'), 'Top 10 drzwi w grach', 'Wszyscy kochamy drzwi: obrotowe, drewniane, wybuchające. Jednak 10 z nich na zawsze zapadło nam w pamięci.', 'g+H8C6YT', 2, '2014-11-07 06:14:00.742000000', 'A')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, dtype) VALUES (nextval('content_sequence'), 'Kraby w grach', 'Wyobraź sobie: odkrywasz nową wyspę. Znikąd oznak życia, wszędzie tylko busz. Zbierasz w spokoju grzyby, a tu nagle: ATAKUJE CIĘ PAN KRAB!!! Przerażające doświadczenie każdego, kto choć raz grał w Subnautice. Ale to tylko jedna z gier, gdzie kraby nie mają przyjacielskich zamiarów...', 'E8R4Nq7j', 3, '2017-12-24 06:14:00.742000000', 'A')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, dtype) VALUES (nextval('content_sequence'), 'Czy Dying Light 2 spełniło oczekiwnia?', 'Choć 1 część odbiła się echem na całym świecie, kolejna odsłona gry może temu sukcesowi nie dorównać. Pierwsze opinie na temat gry są różne. Dowiedzmy się co o grze sądzą eksperci.', 'nW9sUwDr', 3, '2007-05-14 06:14:00.742000000', 'A')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, dtype) VALUES (nextval('content_sequence'), 'Kiedy Wiedźmin 4?', 'Wiele osób nie może się doczekać najnowszej odsłony Wiedźmina. Sprawdźmy co o dacie wydania gry mówi producent.', '34pyBJuA', 4, '1997-04-01 06:14:00.742000000', 'A')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, dtype) VALUES (nextval('content_sequence'), 'Dlaczego GTA 6 nigdy nie powstanie', 'Po ogromnym sukcesie ostatniej części Grand Theft Auto fani czekają na wielki powrót. Będą jednak zawiedzeni, bo szansa na wyjście 6 części serii jest bardzo nikła. Dowiedz się co na ten temat sądzą krytycy.', 'A6NP1dAd', 2, '2021-09-17 06:14:00.742000000', 'A')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, dtype) VALUES (nextval('content_sequence'), 'Czy Cyberpunk 2077 został wydany zbyt wcześnie', 'Przerażająca liczba bugów w grze powoduje u wielu graczy nietęgie miny. Czy gdyby premierę przełożyć o rok liczba zadowolonych klientów byłaby większa?', 'rI3_Fh+Z', 2, '2022-08-27 06:14:00.742000000', 'A')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, game_publish_date, developer, editor_score, dtype) VALUES (nextval('content_sequence'), 'GTA V', 'Taka gra, że się strzela i jeździ i parodiuje ameryke', 'HO5uDuyZ', 3, '2013-09-17 06:14:00.742000000', '2013-09-17 06:14:00.742000000', 'Rockstar', 9.2, 'G')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, game_publish_date, developer, editor_score, dtype) VALUES (nextval('content_sequence'), 'Wiedźmin 3: Dziki Gon', 'Najnowsza część przygód Geralta z Rivii, w najlepszym do tej pory wydaniu.', 'h7eTElzv', 2, '2015-05-18 06:14:00.742000000', '2015-05-18 06:14:00.742000000', 'CD Projekt Red', 10, 'G')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, game_publish_date, developer, editor_score, dtype) VALUES (nextval('content_sequence'), 'Subnautica', 'Idealna gra dla miłośników przygód podwodnych oraz chętnych do przezwyciężenia swojej talassofobii.', 'TESEmytG', 4, '2015-01-06 06:14:00.742000000', '2014-12-16 06:14:00.742000000', 'Unknown Worlds', 8.2, 'G')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, game_publish_date, developer, editor_score, dtype) VALUES (nextval('content_sequence'), 'Minecraft', 'Gra o klockach oraz zombie: wykaż się kreatywnością albo umiejętnościami survival!', 'ZhvybHIJ', 2, '2013-06-29 06:14:00.742000000', '2011-11-18 06:14:00.742000000', 'Markus Persson', 7.7, 'G')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, game_publish_date, developer, editor_score, dtype) VALUES (nextval('content_sequence'), 'Death Stranding', 'Przygodowa gra akcji której fabuła opiera się na tematach związanych z logistyką oraz dostawami między miastami.', '3BYvgcGJ', 4, '2019-11-14 06:14:00.742000000', '2019-11-08 06:14:00.742000000', 'Kojima', 4.5, 'G')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, game_publish_date, developer, editor_score, dtype) VALUES (nextval('content_sequence'), 'Hollow Knight', 'Platformówka 2D o niezapomnianym klimacie', '9Hsa83lL', 4, '2019-11-14 06:14:00.742000000', '2017-02-24 06:14:00.742000000', 'Team Cherry', 8.7, 'G')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, score, game_id, dtype) VALUES (nextval('content_sequence'), 'GTA V - najlepsza odsłona serii', 'San Andreas to już przeszłość: Od dziś wszyscy możemy znowu wyjść na ulicę i okraść nasz ulubiony sklep, tym razem w grafice najnowszej generacji, z całkiem nowymi możliwościami. To część dla wszystkich graczy, którzy godzinami szukali taksówki, żeby grzecznie wyprowadzić z niej kierowcę i samemu objechać cały kontynent.', 'UzheV7r6', 1, '2013-10-03 06:14:00.742000000', 9.4, 7, 'R')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, score, game_id, dtype) VALUES (nextval('content_sequence'), 'Wiedźmin 3, czyli jak robić RPGi', 'CD Projekt Red znowu strzeliło w samą dziesiątkę: ich najnowsza produkcja, Wiedźmin 3, odhacza wszystkie wymogi do bycia nazywaną grą tego roku, jeśli nie nawet tej dekady. Dawno tak bardzo nie wczułem się w świat i otaczający mnie klimat, jak w tej grze.', '47FJ3kt3', 1, '2015-07-29 06:14:00.742000000', 10, 8, 'R')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, score, game_id, dtype) VALUES (nextval('content_sequence'), 'Subnautica. Oceany nie takie bezpieczne!', 'Jeżeli jedyne, co wam się kojarzy z oceanem, to urocze pingwinki oraz delfiny komunikujące się echolokacją, Subnautica będzie dla was prawdziwym szokiem. Nigdy wcześniej nie zostało tak dobrze oddane uczucie bezradności oraz strachu przed głębinami morskimi, oraz potworami, które tam na nas czychają...', '210EGcDe', 1, '2017-03-10 06:14:00.742000000', 7.9, 9, 'R')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, score, game_id, dtype) VALUES (nextval('content_sequence'), 'Death Stranding - kto w to gra??!', 'Kiedy miałem 6 lat, pamiętam jak dużo frajdy sprawiało mi patrzenie, kiedy moja babcia malowała po kolei ściany mieszkania tym samym odcieniem koloru białego. Jeżeli wy też wspominacie czule te czasy, z pewnością spodoba wam się nowa gra Kojimy, w której na każdą godzinę rozgrywki przypada 55 minut chodzenia z punktu A do punktu B.', '+JpfUmOg', 2, '2019-12-25 06:14:00.742000000', 2.5, 11, 'R')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, score, game_id, dtype) VALUES (nextval('content_sequence'), 'Death Stranding to niedocenione arcydzieło', 'Wielu graczy po zagraniu w najnowszy tytuł Kojimy stwierdziło, że jest to nudna gra. Dla mnie był to jeden z najprzyjemniejszych tytułów ostatnich lat.', 'pOta72hD', 3, '2020-01-03 06:14:00.742000000', 7.7, 11, 'R')
INSERT INTO content (id, title, introduction, path, author_id, publish_date, score, game_id, dtype) VALUES (nextval('content_sequence'), 'Hollow Knight - super!', 'Prawdziwa uczta dla fanów metroidvanii.', '72oaJdu2', 4, '2017-03-13 06:14:00.742000000', 9.1, 12, 'R')
